import { Box, Center, Grid } from "@chakra-ui/layout";
import { useState } from "react";

function Calandre(rowNumber, props) {
  const Hours = [
    "00:00",
    "01:00",
    "02:00",
    "03:00",
    "04:00",
    "05:00",
    "06:00",
    "07:00",
    "08:00",
    "09:00",
    "10:00",
    "11:00",
    "12:00",
    "13:00",
    "14:00",
    "15:00",
    "16:00",
    "17:00",
    "18:00",
    "19:00",
    "20:00",
    "21:00",
    "22:00",
    "23:00",
  ];

  //to get format yyy-mm-ddThh:mm:ss
  // .toISOString().slice(0, 19)

  const [date, setDate] = useState(new Date());

  const addDays = (date, days) => {
    var result = new Date(date);
    result.setDate(result.getDate() + days);
    return result;
  };

  const elements = [];
  //make header
  for (let i = 0; i < rowNumber.rowNumber; i++) {
    if (i === 0) {
      elements.push(date);
      // elements.push(new date());
    } else {
      console.log(elements[elements.length - 1]);
      elements.push(addDays(elements[elements.length - 1], 1));
    }
  }

  const items = [];

  return (
    <Grid templateColumns="repeat(5, 1fr)" gap={1}>
      <Box w="100%" bg="blue.500">
        <Center h="160px" bg="red.500">
          /
        </Center>
        {Hours.map((value) => (
          <Center h="160px" key={value} fontSize="20px">
            {value}
          </Center>
        ))}
      </Box>
      {items}
    </Grid>
  );
}

export default Calandre;
