import { Button, IconButton } from "@chakra-ui/button";
import { Box } from "@chakra-ui/layout";
import { CloseIcon } from "@chakra-ui/icons";
const Task = (props) => {
  const { taskvalue, task } = props;
  const remove = () => {
    task.splice(task.indexOf(taskvalue), 1);
  };
  return (
    <Box bg="red" mx={3} onClick={() => remove()} px={2}>
      just test
      <Button
        maxH="10px"
        maxW="10px"
        // colorScheme="teal"
        // variant="outline"

        fontSize="10px"
      >
        <CloseIcon />
      </Button>
    </Box>
  );
};
export default Task;
