import { Box, Flex, Text } from "@chakra-ui/layout";
import { Grid } from "@chakra-ui/layout";
import { GridItem } from "@chakra-ui/layout";
import Task from "./../taks";

const TimeElement = (props) => {
  const { HoursValue, value, addtaks, task } = props;
  return (
    <Box h="160px" key={HoursValue} fontSize="20px">
      <Grid
        h="100%"
        templateRows="repeat(4, 1fr)"
        border="5px"
        borderColor="gray.200"
        gap={0}
      >
        <GridItem
          maxH="40px"
          onClick={() =>
            addtaks(
              value.toISOString().slice(0, 10) +
                "T" +
                HoursValue.slice(0, 3) +
                "00",
              value.toISOString().slice(0, 10) +
                "T" +
                HoursValue.slice(0, 3) +
                "15"
            )
          }
          border="1px"
          id={
            value.toISOString().slice(0, 10) +
            "T" +
            HoursValue.slice(0, 3) +
            "00"
          }
          //   bgColor="gray.50"
          borderColor="gray.200"
        >
          <Flex>
            <Text fontSize="xs">{HoursValue.slice(0, 3) + "15"}</Text>
            {task.map((taskvalue) =>
              taskvalue.start ===
              value.toISOString().slice(0, 10) +
                "T" +
                HoursValue.slice(0, 3) +
                "00" ? (
                <Task />
              ) : (
                ``
              )
            )}
          </Flex>
        </GridItem>
        <GridItem
          border="1px"
          id={
            value.toISOString().slice(0, 10) +
            "T" +
            HoursValue.slice(0, 3) +
            "15"
          }
          bgColor="gray.50"
          borderColor="gray.200"
        ></GridItem>
        <GridItem
          border="1px"
          id={
            value.toISOString().slice(0, 10) +
            "T" +
            HoursValue.slice(0, 3) +
            "30"
          }
          bgColor="gray.50"
          borderColor="gray.200"
        ></GridItem>
        <GridItem
          border="1px"
          id={
            value.toISOString().slice(0, 10) +
            "T" +
            HoursValue.slice(0, 3) +
            "45"
          }
          bgColor="gray.50"
          borderColor="gray.200"
        ></GridItem>
      </Grid>
    </Box>
  );
};
export default TimeElement;
