import { IconButton } from "@chakra-ui/button";
import { Box } from "@chakra-ui/layout";
import { CloseIcon } from "@chakra-ui/icons";
const Task = (props) => {
  const { taskvalue, task } = props;
  const remove = () => {
    task.splice(task.indexOf(taskvalue), 1);
  };
  return (
    <Box bg="red" mx={3} px={2}>
      just test
      <InputRightElement>
        <Button
          bgColor="transparent"
          size="sm"
          onClick={() => remove()}
          icon={<CloseIcon />}
        >
          <CloseIcon /> }
        </Button>
      </InputRightElement>
      <IconButton
        maxH="10px"
        maxW="10px"
        variant="outline"
        colorScheme="teal"
        aria-label="Call Sage"
        fontSize="10px"
      />
    </Box>
  );
};
export default Task;
